#include <iostream>#include "Figure.h"using namespace std;Figure::Figure(int x, int y, int width, int height){	Move(x, y);	Resize(width, height);}Figure::~Figure(){}void Figure::Move(int x, int y){	this->x = x;	this->y = y;}void Figure::Resize(int width, int height){	this->width = width;	this->height = height;}void Figure::Draw(){	cout << "Figure::Draw" << endl;}Ellipse::Ellipse(int x, int y, int width, int height)	: Figure(x, y, width, height){}
